import React, {FunctionComponent, useState } from 'react';
import POKEMONS from './models/mock-pokemon';

const App: FunctionComponent = () => {
 const [name, setName] = useState<String>('React'); // <String> => typage des données sauvegardé dans le useState
 const pokemons = POKEMONS;

 return (
  <div>
  <h1>Bonjour, {name} !</h1>
  <p>Il y a {pokemons.length} pokémons dans le Pokédex.</p>
  {pokemons.map((pokemon) => (
    <div>
    <h3>{pokemon.name}</h3>
    <img src= {pokemon.picture} alt="picture-{pokemon.name}"/>
      <ul>
        <li>id : {pokemon.id}</li>
        <li>type(s) : {pokemon.types}</li>
        <li>HP : {pokemon.hp}</li>
        <li>CP : {pokemon.cp}</li>
      </ul>
    </div>
  )
  )}
  </div>
 )
}

export default App;
