import React, {FunctionComponent, useState, useEffect } from 'react';
import Pokemon from './models/pokemon';
import POKEMONS from './models/mock-pokemon';

const App: FunctionComponent = () => {
 const [name, setName] = useState<String>('React'); // <String> => typage des données sauvegardé dans le useState
 const [pokemons, setPokemons] = useState<Pokemon[]>([]);

 useEffect(() => {
  setPokemons(POKEMONS)}, []);

 return (
  <div>
    <h1>Bonjour, {name} !</h1>
    <p>Il y a {pokemons.length} pokémons dans le Pokédex.</p>


  {pokemons.map((pokemon) => (
    <div className="card" key={`${pokemon.id}-${pokemon.name}`}>
      <div className="card-image waves-effect waves-block waves-light">
        <img className="activator" src={pokemon.picture} />
      </div>
      <div className="card-content">
        <span className="card-title activator grey-text text-darken-4">{pokemon.name}<i className="material-icons right">more_vert</i></span>
        <p>ID : {pokemon.id}</p>
      </div>
      <div className="card-reveal">
        <span className="card-title grey-text text-darken-4">Card Title<i className="material-icons right">close</i></span>
        <p>HP : {pokemon.hp}</p>
        <p>CP : {pokemon.cp}</p>
        <p>TYPE(S) : {pokemon.types}</p>
        <p>CREATED : {pokemon.created.toString()}</p>
      </div>
    </div>
    ))
    }
  </div>

 )
}

export default App;
